targets.expr:
topk(10, sum by(bucketName) (
    count_over_time({namespace="${namespace}", pod=~"${pod}-.*"}
                    | json | bucketName!="" and httpCode="404"
                    [$__interval])
))

topk(10, sum by(bucketName) (
    count_over_time({namespace="${namespace}", pod=~"${pod}-.*"}
                    | json | bucketName!="" and httpCode="500"
                    [$__interval])
))

topk(10, sum by(bucketName) (
    count_over_time({namespace="${namespace}", pod=~"${pod}-.*"}
                    | json | bucketName!="" and httpCode=~"5.."
                    [$__interval])
))
