targets.expr:
time()
- max by() (cloud_server_last_report_timestamp{namespace="${namespace}", job="${reportJob}"})
+ (cloud_server_last_report_timestamp{namespace="${namespace}", job="${reportJob}"}
   - on() kube_cronjob_status_last_schedule_time{namespace="${namespace}", cronjob="${countItemsJob}"}
   > 0 or vector(0))

topk(10, sum by(bucketName) (
    count_over_time({namespace="${namespace}", pod=~"${pod}-.*"}
                    | json | bucketName!="" and httpCode="404"
                    [$__interval])
))

topk(10, sum by(bucketName) (
    count_over_time({namespace="${namespace}", pod=~"${pod}-.*"}
                    | json | bucketName!="" and httpCode="500"
                    [$__interval])
))

topk(10, sum by(bucketName) (
    count_over_time({namespace="${namespace}", pod=~"${pod}-.*"}
                    | json | bucketName!="" and httpCode=~"5.."
                    [$__interval])
))
